{% block title %}Comment index{% endblock %}

{% block body %}
    <h1>Comment index</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>CommentId</th>
                <th>CommentDescription</th>
                <th>CommentDate</th>
                <th>Nblike</th>
                <th>Nbdislike</th>
                <th>CommentImage</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for comment in comments %}
            <tr>
                <td>{{ comment.commentId }}</td>
                <td>{{ comment.commentDescription }}</td>
                <td>{{ comment.commentDate ? comment.commentDate|date('Y-m-d') : '' }}</td>
                <td>{{ comment.nblike }}</td>
                <td>{{ comment.nbdislike }}</td>
                <td>{{ comment.commentImage }}</td>
                <td>
                    <a href=\"{{ path('app_comment_show', {'commentId': comment.commentId}) }}\">show</a>
                    <a href=\"{{ path('app_comment_edit', {'commentId': comment.commentId}) }}\">edit</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"7\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('app_comment_new') }}\">Create new</a>
{% endblock %}
